import React from 'react';
import '../styles/Homepage.css';
import Header from '../components/Header';
import Footer from '../components/Footer';
import FilterSidebar from '../components/FilterSidebar';
import ProductCard from '../components/ProductCard';

function HomePage() {
  const products = [
    { id: 1, image: 'https://via.placeholder.com/150', title: 'PPXOC MILKYWAY DRESS IN...', stock: 0 },
    { id: 2, image: 'https://via.placeholder.com/150', title: 'BACKPACK - PRODUCT NAME', stock: 10 },
    { id: 3, image: 'https://via.placeholder.com/150', title: 'STUFFED TOY - DINO', stock: 5 },
    { id: 4, image: 'https://via.placeholder.com/150', title: 'HAT - PRODUCT NAME', stock: 8 },
  ];

  return (
    <div className="homepage">
      <Header />
      <div className="hero-section">
        <h1>DISCOVER OUR PRODUCTS</h1>
        <p>Lorem ipsum dolor sit amet consectetur. Amet est posuere rhoncus scelerisque. Dolor integer scelerisque nibh amet mi ut elementum dolor.</p>
      </div>
      <div className="main-content">
        <FilterSidebar />
        <div className="product-grid">
          <div className="sorting-dropdown">
            <select>
              <option value="recommended">Recommended</option>
              <option value="newest">Newest First</option>
              <option value="popular">Popular</option>
              <option value="price-high">Price: High to Low</option>
              <option value="price-low">Price: Low to High</option>
            </select>
          </div>
          <div className="products-container">
            {products.map(product => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default HomePage;
